import React from 'react';

class Main extends React.Component {

    handleEditAvatarClick() {
        document.querySelector('.popup_profile').classList.add('popup_opened')
    }

    handleEditProfileClick() {
        document.querySelector('.popup_profile-avatar').classList.add('popup_opened')
    }

    handleAddPlaceClick() {
        document.querySelector('.popup_new-card').classList.add('popup_opened')
    }


    render(){
        return(
            <main className="main">
                    <section className="profile">
                        <div className="profile__block">
                            <div className="profile__avatar">
                                <img className="profile__photo" src="" alt="Аватар"/>
                                <div className="profile__photo-overllay" onClick={this.handleEditAvatarClick}></div>
                            </div>
                            <div className="profile__info">
                                <h1 className="profile__name"></h1>
                                <button className="profile__button-edit" type="button" onClick={this.handleEditProfileClick}></button>
                                <p className="profile__job"></p>
                            </div>
                        </div>
                        <button className="profile__button-add" type="button" onClick={this.handleAddPlaceClick}></button>
                    </section>
                    <section className="elements">
                    </section>
                </main>
        )
    }
}

export default Main
